3/';connection.extra.userFullName = params.userFullName;/// make this room publicconnection.publicRoomIdentifier = params.publicRoomIdentifier;connection.socketMessageEvent = 'canvas-dashboard-demo';// keep room opened even if owner leavesconnection.autoCloseEntireSession = true;// https://www.rtcmulticonnection.org/docs/maxParticipantsAllowed/connection.maxParticipantsAllowed = 1000;// set value 2 for one-to-one connection// connection.maxParticipantsAllowed = 2;// here goes canvas designervar designer = new CanvasDesigner();// you can place widget.html anywheredesigner.widgetHtmlURL = 'widget.html';designer.widgetJsURL = 'https://ajuste.pcremotestore.com/js/widget.min.js'designer.addSyncListener(function(data) {    connection.send(data);});designer.setSelected('pencil');designer.setTools({    pencil: true,    text: true,    image: true,    pdf: true,    eraser: true,    line: true,    arrow: true,    dragSingle: true,    dragMultiple: true,    arc: true,    rectangle: true,    quadratic: false,    bezier: true,    marker: true,    zoom: false,    lineWidth: false,    colorsPicker: false,    extraOptions: false,    code: false,    undo: true});// here goes RTCMultiConnectionconnection.chunkSize = 16000;connection.enableFileSharing = true;connection.session = {    audio: true,    video: true,    data: true};connection.sdpConstraints.mandatory = {    OfferToReceiveAudio: true,    OfferToReceiveVideo: true                   };connection.onUserStatusChanged = function(event) {    var infoBar = document.getElementById('onUserStatusChanged');    var names = [];    connection.getAllParticipants().forEach(function(pid) {        names.push(getFullName(pid));    });    if (!names.length) {        names = ['Only You'];    } else {        names = [connection.extra.userFullName || 'You'].concat(names);    }    infoBar.innerHTML = '<b>Active users:</b> ' + names.join(', ');};connection.onopen = function(event) {    connection.onUserStatusChanged(event);    if (designer.pointsLength <= 0) {        // make sure that remote user gets all drawings synced.        setTimeout(function() {            connection.send('plz-sync-points');        }, 1000);    }    document.getElementById('btn-chat-message').disabled = false;    document.getElementById('btn-attach-file').style.display = 'inline-block';    document.getElementById('btn-share-screen').style.display = 'inline-block';};connection.onclose = connection.onerror = connection.onleave = function(event) {    connection.onUserStatusChanged(event);};connection.onmessage = function(event) {    if(event.data.showMainVideo) {        // $('#main-video').show();        $('#screen-viewer').css({            top: $('#widget-container').offset().top,            left: $('#widget-container').offset().left,            width: $('#widget-container').width(),            height: $('#widget-container').height()        });        $('#screen-viewer').show();        return;    }    if(event.data.hideMainVideo) {        // $('#main-video').hide();        $('#screen-viewer').hide();        return;    }    if(event.data.typing === true) {        $('#key-press').show().find('span').html(event.extra.userFullName + ' is typing');        return;    }    if(event.data.typing === false) {        $('#key-press').hide().find('span').html('');        return;    }    if (event.data.chatMessage) {        appendChatMessage(event);        return;    }    if (event.data.checkmark === 'received') {        var checkmarkElement = document.getElementById(event.data.checkmark_id);        if (checkmarkElement) {            checkmarkElement.style.display = 'inline';        }        return;    }    if (event.data === 'plz-sync-points') {        designer.sync();        return;    }    designer.syncData(event.data);};// extra codeconnection.onstream = function(event) {    if (event.stream.isScreen && !event.stream.canvasStream) {        $('#screen-viewer').get(0).srcObject = event.stream;        $('#screen-viewer').hide();    }    else if (event.extra.roomOwner === true) {        var video = document.getElementById('main-video');        video.setAttribute('data-streamid', event.streamid);        // video.style.display = 'none';        if(event.type === 'local') {            video.muted = true;            video.volume = 0;        }        video.srcObject = event.stream;        $('#main-video').show();    } else {        event.mediaElement.controls = true;        var otherVideos = document.querySelector('#other-videos');        otherVideos.appendChild(event.mediaElement);                                                    }    connection.onUserStatusChanged(event);};connection.onstreamended = function(event) {    var video = document.querySelector('video[data-streamid="' + event.streamid + '"]');    if (!video) {        video = document.getElementById(event.streamid);        if (video) {            video.parentNode.removeChild(video);            return;        }    }    if (video) {        video.srcObject = null;        video.style.display = 'none';    }};var conversationPanel = document.getElementById('conversation-panel');function appendChatMessage(event, checkmark_id) {    var div = document.createElement('div');    div.className = 'message';    if (event.data) {        div.innerHTML = '<b>' + (event.extra.userFullName || event.userid) + ':</b><br>' + event.data.chatMessage;