        }    }});window.onkeyup = function(e) {    var code = e.keyCode || e.which;    if (code == 13) {        $('#btn-chat-message').click();    }};document.getElementById('btn-chat-message').onclick = function() {    var chatMessage = $('.emojionearea-editor').html();    $('.emojionearea-editor').html('');    if (!chatMessage || !chatMessage.replace(/ /g, '').length) return;    var checkmark_id = connection.userid + connection.token();    appendChatMessage(chatMessage, checkmark_id);    connection.send({        chatMessage: chatMessage,        checkmark_id: checkmark_id    });    connection.send({        typing: false    });};var recentFile;document.getElementById('btn-attach-file').onclick = function() {    var file = new FileSelector();    file.selectSingleFile(function(file) {        recentFile = file;        if(connection.getAllParticipants().length >= 1) {            recentFile.userIndex = 0;            connection.send(file, connection.getAllParticipants()[recentFile.userIndex]);        }    });};function getFileHTML(file) {    var url = file.url || URL.createObjectURL(file);    var attachment = '<a href="' + url + '" target="_blank" download="' + file.name + '">Download: <b>' + file.name + '</b></a>';    if (file.name.match(/\.jpg|\.png|\.jpeg|\.gif/gi)) {        attachment += '<br><img crossOrigin="anonymous" src="' + url + '">';    } else if (file.name.match(/\.wav|\.mp3/gi)) {        attachment += '<br><audio src="' + url + '" controls></audio>';    } else if (file.name.match(/\.pdf|\.js|\.txt|\.sh/gi)) {       attachment += '<iframe class="inline-iframe" src="' + url + '"></iframe></a>';         }    return attachment;}function getFullName(userid) {    var _userFullName = userid;    if (connection.peers[userid] && connection.peers[userid].extra.userFullName) {        _userFullName = connection.peers[userid].extra.userFullName;    }    return _userFullName;}connection.onFileEnd = function(file) {    var html = getFileHTML(file);    var div = progressHelper[file.uuid].div;    if (file.userid === connection.userid) {        div.innerHTML = '<b>You:</b><br>' + html;        div.style.background = '#cbffcb';        if(recentFile) {            recentFile.userIndex++;            var nextUserId = connection.getAllParticipants()[recentFile.userIndex];            if(nextUserId) {                connection.send(recentFile, nextUserId);            }            else {                recentFile = null;            }        }        else {            recentFile = null;        }    } else {        div.innerHTML = '<b>' + getFullName(file.userid) + ':</b><br>' + html;    }};// to make sure file-saver dialog is not invoked.connection.autoSaveToDisk = false;var progressHelper = {};connection.onFileProgress = function(chunk, uuid) {    var helper = progressHelper[chunk.uuid];    helper.progress.value = chunk.currentPosition || chunk.maxChunks || helper.progress.max;    updateLabel(helper.progress, helper.label);};connection.onFileStart = function(file) {    var div = document.createElement('div');    div.className = 'message';    if (file.userid === connection.userid) {        var userFullName = file.remoteUserId;        if(connection.peersBackup[file.remoteUserId]) {            userFullName = connection.peersBackup[file.remoteUserId].extra.userFullName;        }        div.innerHTML = '<b>You (to: ' + userFullName + '):</b><br><label>0%</label> <progress></progress>';        div.style.background = '#cbffcb';    } else {        div.innerHTML = '<b>' + getFullName(file.userid) + ':</b><br><label>0%</label> <progress></progress>';    }    div.title = file.name;    conversationPanel.appendChild(div);    progressHelper[file.uuid] = {        div: div,        progress: div.querySelector('progress'),        label: div.querySelector('label')    };    progressHelper[file.uuid].progress.max = file.maxChunks;    conversationPanel.scrollTop = conversationPanel.clientHeight;    conversationPanel.scrollTop = conversationPanel.scrollHeight - conversationPanel.scrollTop;};function updateLabel(progress, label) {    if (progress.position == -1) return;    var position = +progress.position.toFixed(2).split('.')[1] || 100;    label.innerHTML = position + '%';}if(!!params.password) {    connection.password = params.password;}designer.appendTo(document.getElementById('widget-container'), function() {    if (params.open === true || params.open === 'true') {            var tempStreamCanvas = document.getElementById('temp-stream-canvas');            var tempStream = tempStreamCanvas.captureStream();            tempStream.isScreen = true;            tempStream.streamid = tempStream.id;            tempStream.type = 'local';            connection.attachStreams.push(tempStream);            window.tempStream = tempStream;            connection.extra.roomOwner = true;            connection.open(params.sessionid, function(isRoomOpened, roomid, error) {                if (error) {                    if (error === connection.errors.ROOM_NOT_AVAILABLE) {                        alert('Alguien ya esta usando esta sala. Porfavor ingrese otro Folio para la sala.');                        return;                    }                    alert(error);                }                connection.socket.on('disconnect', function() {                    location.reload();                });            });    } else {        connection.join(params.sessionid, function(isRoomJoined, roomid, error) {            if (error) {                if (error === connection.errors.ROOM_NOT_AVAILABLE) {                    alert('Esta sala no existe aun. Por favor, espere a que el moderador ingrese a la sala.');                    return;                }                if (error === connection.errors.ROOM_FULL) {                    alert('Room is full.');                    return;                }                if (error === connection.errors.INVALID_PASSWORD) {                    connection.password = prompt('Please enter room password.') || '';                    if(!connection.password.length) {                        alert('Invalid password.');                        return;                    }                    connection.join(params.sessionid, function(isRoomJoined, roomid, error) {                        if(error) {                            alert(error);                        }                    });                    return;                }                alert(error);            }            connection.socket.on('disconnect', function() {                location.reload();            });        });    }});function addStreamStopListener(stream, callback) {    stream.addEventListener('ended', function() {        callback();        callback = function() {};    }, false);    stream.addEventListener('inactive', function() {        callback();        callback = function() {};    }, false);    stream.getTracks().forEach(function(track) {        track.addEventListener('ended', function() {            callback();            callback = function() {};        }, false);        track.addEventListener('inactive', function() {            callback();            callback = function() {};        }, false);    });}function replaceTrack(videoTrack, screenTrackId) {    if (!videoTrack) return;    if (videoTrack.readyState === 'ended') {        alert('Can not replace an "ended" track. track.readyState: ' + videoTrack.readyState);        return;    }    connection.getAllParticipants().forEach(function(pid) {        var peer = connection.peers[pid].peer;        if (!peer.getSenders) return;        var trackToReplace = videoTrack;        peer.getSenders().forEach(function(sender) {            if (!sender || !sender.track) return;            if(screenTrackId) {                if(trackToReplace && sender.track.id === screenTrackId) {                    sender.replaceTrack(trackToReplace);                    trackToReplace = null;                }                return;            }            if(sender.track.id !== tempStream.getTracks()[0].id) return;            if (sender.track.kind === 'video' && trackToReplace) {                sender.replaceTrack(trackToReplace);                trackToReplace = null;            }        });    });}function replaceScreenTrack(stream) {    stream.isScreen = true;    stream.streamid = stream.id;    stream.type = 'local';    // connection.attachStreams.push(stream);    connection.onstream({        stream: stream,        type: 'local',        streamid: stream.id,        // mediaElement: video    });    var screenTrackId = stream.getTracks()[0].id;    addStreamStopListener(stream, function() {        connection.send({            hideMainVideo: true        });        // $('#main-video').hide();        $('#screen-viewer').hide();        $('#btn-share-screen').show();        replaceTrack(tempStream.getTracks()[0], screenTrackId);    });    stream.getTracks().forEach(function(track) {        if(track.kind === 'video' && track.readyState === 'live') {            replaceTrack(track);        }    });    connection.send({        showMainVideo: true    });    // $('#main-video').show();    $('#screen-viewer').css({            top: $('#widget-container').offset().top,            left: $('#widget-container').offset().left,            width: $('#widget-container').width(),            height: $('#widget-container').height()        });    $('#screen-viewer').show();}$('#btn-share-screen').click(function() {    if(!window.tempStream) {        alert('Screen sharing is not enabled.');        return;    }    $('#btn-share-screen').hide();    if(navigator.mediaDevices.getDisplayMedia) {        navigator.mediaDevices.getDisplayMedia(screen_constraints).then(stream => {            replaceScreenTrack(stream);        }, error => {            alert('Please make sure to use Edge 17 or higher.');        });    }    else if(navigator.getDisplayMedia) {        navigator.getDisplayMedia(screen_constraints).then(stream => {            replaceScreenTrack(stream);        }, error => {            alert('Please make sure to use Edge 17 or higher.');        });    }    else {        alert('getDisplayMedia API is not available in this browser.');    }});</script><script type="text/javascript" src="https://cdn.jsdelivr.net/npm/html2canvas@1.0.0-rc.1/dist/html2canvas.min.js"></script><script>/** * Ejemplo 4 de html2canvas para convertir el HTML de una web * a un elemento canvas - Descargar la captura como imagen PNG *  * @author parzibyte *///Definimos el botón para escuchar su clickconst $boton = document.querySelector("#download_signature"), // El botón que desencadena $objetivo =document.querySelector("#contenedor2");// A qué le tomamos la fotocanvas//$objetivo = document.body; // A qué le tomamos la fotocanvas// Nota: no necesitamos contenedor, pues vamos a descargarla// Agregar el listener al botón$boton.addEventListener("click", () => {  html2canvas($objetivo) // Llamar a html2canvas y pasarle el elemento    .then(canvas => {